#include <iostream>using namespace std;int main (){  int T;  cout << "Please enter the temperature in Fahrenheit degrees... " << endl;  cin>>T;  int C;  C=(5*(T-32))/9;  cout << "The temperature in Celsius degrees is " << C << endl;  if (C<100) {    cout << "Water does not boil at this temperature." << endl;  } else {    cout << "Water boils at this temperature." << endl;  }}